import React from 'react';
import styled from 'styled-components';
import { FaStar } from 'react-icons/fa';

const FeedbackListContainer = styled.div`
  margin: 40px 0;
`;

const FeedbackGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 30px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const FeedbackCard = styled.div`
  background-color: white;
  border-radius: 10px;
  padding: 25px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
  transition: transform 0.3s ease;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
  }
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 15px;
`;

const UserAvatar = styled.div`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background-color: #f1f1f1;
  margin-right: 15px;
  overflow: hidden;
  
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const UserName = styled.h4`
  margin: 0;
  color: #2c3e50;
  font-size: 1.1rem;
`;

const UserDate = styled.p`
  margin: 5px 0 0;
  color: #7f8c8d;
  font-size: 0.9rem;
`;

const Rating = styled.div`
  display: flex;
  color: #f39c12;
  margin-bottom: 15px;
`;

const FeedbackText = styled.p`
  color: #7f8c8d;
  line-height: 1.6;
  margin: 0;
`;

const FeedbackList = () => {
  // Sample feedback data
  const feedbacks = [
    {
      id: 1,
      name: "Emma Thompson",
      date: "October 15, 2023",
      rating: 5,
      text: "MindMentor has completely transformed my study habits! The AI recommendations are spot-on, and I love how it adapts to my mood and stress levels. My grades have improved significantly since I started using it.",
      avatar: "https://randomuser.me/api/portraits/women/44.jpg"
    },
    {
      id: 2,
      name: "Michael Chen",
      date: "September 28, 2023",
      rating: 4,
      text: "As a teacher, I find the dashboard incredibly useful for monitoring student engagement. The early warning system for stress has helped me identify students who need additional support before issues escalate.",
      avatar: "https://randomuser.me/api/portraits/men/32.jpg"
    },
    {
      id: 3,
      name: "Sarah Johnson",
      date: "October 3, 2023",
      rating: 5,
      text: "The music recommendation feature is my favorite! It somehow always knows exactly what I need to hear to focus or calm down. The interface is intuitive and the app runs smoothly.",
      avatar: "https://randomuser.me/api/portraits/women/68.jpg"
    },
    {
      id: 4,
      name: "David Wilson",
      date: "September 15, 2023",
      rating: 4,
      text: "Our university implemented MindMentor last semester, and we've seen a notable decrease in reported stress levels among students. The analytics provide valuable insights for our wellness programs.",
      avatar: "https://randomuser.me/api/portraits/men/75.jpg"
    }
  ];

  return (
    <FeedbackListContainer>
      <FeedbackGrid>
        {feedbacks.map(feedback => (
          <FeedbackCard key={feedback.id}>
            <UserInfo>
              <UserAvatar>
                <img src={feedback.avatar} alt={feedback.name} />
              </UserAvatar>
              <div>
                <UserName>{feedback.name}</UserName>
                <UserDate>{feedback.date}</UserDate>
              </div>
            </UserInfo>
            
            <Rating>
              {[...Array(5)].map((_, i) => (
                <FaStar key={i} color={i < feedback.rating ? '#f39c12' : '#ddd'} />
              ))}
            </Rating>
            
            <FeedbackText>{feedback.text}</FeedbackText>
          </FeedbackCard>
        ))}
      </FeedbackGrid>
    </FeedbackListContainer>
  );
};

export default FeedbackList;
