    }
  ];

  return (
    <StatsSection>
      <StatsContainer>
        {statsData.map((stat, index) => (
          <StatItem key={index}>
            <h3>{stat.count}</h3>
            <p>{stat.label}</p>
          </StatItem>
        ))}
      </StatsContainer>
    </StatsSection>
  );
};

export default Stats;
