import React from 'react';
import styled from 'styled-components';
import { FaGooglePlay, FaApple, FaWindows, FaLinux, FaDesktop } from 'react-icons/fa';
import appScreenshot from '../assets/images/student-interface.jpg';

const PageContainer = styled.div`
  padding-top: 60px;
`;

const HeroSection = styled.section`
  background: linear-gradient(135deg, var(--gradient-start) 0%, var(--gradient-end) 100%);
  color: white;
  padding: 100px 0 50px;
  text-align: center;
`;

const HeroTitle = styled.h1`
  font-size: 3rem;
  margin-bottom: 20px;
  
  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const HeroSubtitle = styled.p`
  font-size: 1.2rem;
  max-width: 700px;
  margin: 0 auto 40px;
  opacity: 0.9;
`;

const DownloadSection = styled.section`
  padding: 80px 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 50px;
  color: var(--text-color);
`;

const PlatformsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;
  
  @media (max-width: 992px) {
    grid-template-columns: repeat(2, 1fr);
  }
  
  @media (max-width: 576px) {
    grid-template-columns: 1fr;
  }
`;

const PlatformCard = styled.div`
  background-color: white;
  border-radius: 10px;
  padding: 40px 30px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
  text-align: center;
  transition: transform 0.3s ease;
  
  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
  }
`;

const PlatformIcon = styled.div`
  font-size: 3rem;
  color: var(--primary-color);
  margin-bottom: 20px;
`;

const PlatformTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 15px;
  color: var(--text-color);
`;

const PlatformDescription = styled.p`
  color: var(--light-text);
  margin-bottom: 25px;
`;

const DownloadButton = styled.a`
  display: inline-flex;
  align-items: center;
  background-color: var(--primary-color);
  color: white;
  padding: 12px 25px;
  border-radius: 8px;
  text-decoration: none;
  font-weight: 600;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: #0095D9;
  }
  
  svg {
    margin-right: 10px;
  }
`;

const MobileAppsSection = styled.section`
  background-color: var(--section-bg);
  padding: 80px 0;
`;

const MobileAppsContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 50px;
  
  @media (max-width: 992px) {
    flex-direction: column;
  }
`;

const MobileAppsContent = styled.div`
  flex: 1;
`;

const MobileAppsImage = styled.div`
  flex: 1;
  
  img {
    max-width: 100%;
    border-radius: 10px;
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
  }
`;

const AppStoreButtons = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 30px;
  
  @media (max-width: 576px) {
    flex-direction: column;
  }
`;

const AppStoreButton = styled.a`
  display: flex;
  align-items: center;
  background-color: var(--text-color);
  color: white;
  padding: 12px 25px;
  border-radius: 8px;
  text-decoration: none;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: #0095D9;
    transform: translateY(-3px);
  }
  
  svg {
    font-size: 24px;
    margin-right: 10px;
  }
  
  div {
    text-align: left;
  }
  
  span {
    display: block;
    
    &:first-child {
      font-size: 0.8rem;
      opacity: 0.8;
    }
    
    &:last-child {
      font-size: 1.2rem;
      font-weight: 600;
    }
  }
`;

const Download = () => {
  return (
    <PageContainer>
      <HeroSection>
        <Container>
          <HeroTitle>Download MindMentor</HeroTitle>
          <HeroSubtitle>
            Get the latest version of MindMentor on your preferred device and start enhancing your learning experience today.
          </HeroSubtitle>
        </Container>
      </HeroSection>
      
      <DownloadSection>
        <Container>
          <SectionTitle>Available Platforms</SectionTitle>
          
          <PlatformsGrid>
            <PlatformCard>
              <PlatformIcon>
                <FaWindows />
              </PlatformIcon>
              <PlatformTitle>Windows</PlatformTitle>
              <PlatformDescription>
                Download MindMentor for Windows 10 and above. Optimized for desktop experience.
              </PlatformDescription>
              <DownloadButton href="#">
                <FaWindows /> Download for Windows
              </DownloadButton>
            </PlatformCard>
            
            <PlatformCard>
              <PlatformIcon>
                <FaApple />
              </PlatformIcon>
              <PlatformTitle>macOS</PlatformTitle>
              <PlatformDescription>
                Download MindMentor for macOS Big Sur and above. Optimized for Mac experience.
              </PlatformDescription>
              <DownloadButton href="#">
                <FaApple /> Download for Mac
              </DownloadButton>
            </PlatformCard>
            
            <PlatformCard>
              <PlatformIcon>
                <FaLinux />
              </PlatformIcon>
              <PlatformTitle>Linux</PlatformTitle>
              <PlatformDescription>
                Download MindMentor for major Linux distributions. Open-source friendly.
              </PlatformDescription>
              <DownloadButton href="#">
                <FaLinux /> Download for Linux
              </DownloadButton>
            </PlatformCard>
          </PlatformsGrid>
        </Container>
      </DownloadSection>
      
      <MobileAppsSection>
        <Container>
          <MobileAppsContainer>
            <MobileAppsContent>
              <SectionTitle>Mobile Apps</SectionTitle>
              <p>Take MindMentor with you on the go. Our mobile apps offer the same powerful features in a pocket-sized format.</p>
              
              <AppStoreButtons>
                <AppStoreButton href="#">
                  <FaGooglePlay />
                  <div>
                    <span>Get it on</span>
                    <span>Google Play</span>
                  </div>
                </AppStoreButton>
                
                <AppStoreButton href="#">
                  <FaApple />
                  <div>
                    <span>Download on the</span>
                    <span>App Store</span>
                  </div>
                </AppStoreButton>
              </AppStoreButtons>
            </MobileAppsContent>
            
            <MobileAppsImage>
              <img src={appScreenshot} alt="MindMentor Mobile App" />
            </MobileAppsImage>
          </MobileAppsContainer>
        </Container>
      </MobileAppsSection>
      
      <DownloadSection>
        <Container>
