import React from 'react';
import styled from 'styled-components';
import { FaGooglePlay, FaApple } from 'react-icons/fa';
// import appDownloadImage from '../assets/images/app-download.png';

const DownloadSection = styled.section`
  background: linear-gradient(135deg, #e74c3c 0%, #8e44ad 100%);
  color: white;
  padding: 80px 0;
`;

const DownloadContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  display: flex;
  align-items: center;
  
  @media (max-width: 992px) {
    flex-direction: column;
  }
`;

const ContentColumn = styled.div`
  flex: 1;
  
  @media (max-width: 992px) {
    text-align: center;
    margin-bottom: 40px;
  }
`;

const ImageColumn = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  
  img {
    max-width: 100%;
    height: auto;
  }
`;

const Title = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 20px;
  
  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const Description = styled.p`
  font-size: 1.1rem;
  margin-bottom: 30px;
  opacity: 0.9;
  max-width: 600px;
  
  @media (max-width: 992px) {
    margin-left: auto;
    margin-right: auto;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 30px;
  
  @media (max-width: 768px) {
    justify-content: center;
    flex-wrap: wrap;
  }
`;

const DownloadButton = styled.a`
  display: flex;
  align-items: center;
  background-color: #2c3e50;
  color: white;
  padding: 12px 25px;
  border-radius: 8px;
  text-decoration: none;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: #1a252f;
    transform: translateY(-3px);
  }
  
  svg {
    font-size: 24px;
    margin-right: 10px;
  }
  
  div {
    text-align: left;
  }
  
  span {
    display: block;
    
    &:first-child {
      font-size: 0.8rem;
      opacity: 0.8;
    }
    
    &:last-child {
      font-size: 1.2rem;
      font-weight: 600;
    }
  }
`;

// Placeholder image for development
const placeholderImage = "data:image/svg+xml;charset=UTF-8,%3Csvg%20width%3D%22400%22%20height%3D%22600%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20400%20600%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_18e2ea3d7e8%20text%20%7B%20fill%3A%23ffffff%3Bfont-weight%3Abold%3Bfont-family%3AArial%2C%20Helvetica%2C%20Open%20Sans%2C%20sans-serif%2C%20monospace%3Bfont-size%3A20pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_18e2ea3d7e8%22%3E%3Crect%20width%3D%22400%22%20height%3D%22600%22%20fill%3D%22%23e74c3c%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%22120%22%20y%3D%22300%22%3EMindMentor%20App%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E";

const Download = () => {
  return (
    <DownloadSection>
      <DownloadContainer>
        <ImageColumn>
          <img src={placeholderImage} alt="MindMentor App Download" />
        </ImageColumn>
        
        <ContentColumn>
          <Title>Download Latest Version Of The App From</Title>
          <Description>
            Get the latest version of MindMentor on your device for a seamless learning experience.
            Available on both iOS and Android platforms.
          </Description>
          
          <ButtonContainer>
            <DownloadButton href="#">
              <FaGooglePlay />
              <div>
                <span>Get it on</span>
                <span>Google Play</span>
              </div>
            </DownloadButton>
            
            <DownloadButton href="#">
              <FaApple />
              <div>
                <span>Download on the</span>
                <span>App Store</span>
              </div>
            </DownloadButton>
          </ButtonContainer>
        </ContentColumn>
      </DownloadContainer>
    </DownloadSection>
  );
};

export default Download;
